    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
        if(RBDefinido.isSelected() && !RBRango.isSelected() && !RBSinDef.isSelected()){
            c.eliminar(Integer.parseInt(txtElemento.getText()));
            txtResultado.setText(c.toString());
        }else{
            if(!RBDefinido.isSelected() && RBRango.isSelected() && !RBSinDef.isSelected()){
                c.eliminarRango(Integer.parseInt(txtElemento.getText()));
                txtResultado.setText(c.toString());
            }else{
                if(!RBDefinido.isSelected() && !RBRango.isSelected() && RBSinDef.isSelected()){
                    c.eliminar(Integer.parseInt(txtElemento.getText()));
                    txtResultado.setText(c.toString());
                }else{
                    JOptionPane.showMessageDialog(null, "Error de Insertar");
                }
            }
        }
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void btnCrearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearActionPerformed
        if(RBDefinido.isSelected() && !RBRango.isSelected() && !RBSinDef.isSelected()){
             c = new ConjuntoBtw(Integer.parseInt(txtTama.getText()));
            txtResultado.setText(c.toString());
        }else{
            if(!RBDefinido.isSelected() && RBRango.isSelected() && !RBSinDef.isSelected()){
                c = new ConjuntoBtw(Integer.parseInt(txtInicio.getText()), Integer.parseInt(txtFin.getText()));
                txtResultado.setText(c.toString());
            }else{
                if(!RBDefinido.isSelected() && !RBRango.isSelected() && RBSinDef.isSelected()){
                    c = new ConjuntoBtw();
                    txtResultado.setText(c.toString());
                }else{
                    JOptionPane.showMessageDialog(null, "Error de Creacion: eleccion nula o multiple");
                }
            }
        }
    }//GEN-LAST:event_btnCrearActionPerformed

    private void btnEliminarCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarCActionPerformed
        txtResultado.setText("");
    }//GEN-LAST:event_btnEliminarCActionPerformed

    private void btnInsertarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInsertarActionPerformed
        
        if(RBDefinido.isSelected() && !RBRango.isSelected() && !RBSinDef.isSelected()){
            if((Integer.parseInt(txtElemento.getText()) <= Integer.parseInt(txtTama.getText())) && (Integer.parseInt(txtElemento.getText()) > 0)){
                c.insertar(Integer.parseInt(txtElemento.getText()));
                txtResultado.setText(c.toString());
            }else
            JOptionPane.showMessageDialog(null, "El elemento que desea ingresar no forma parte de la dimension creada para el conjunto ");
        }else{
            if(!RBDefinido.isSelected() && RBRango.isSelected() && !RBSinDef.isSelected()){
                if((Integer.parseInt(txtElemento.getText()) <= Integer.parseInt(txtFin.getText())) && (Integer.parseInt(txtElemento.getText()) >= Integer.parseInt(txtInicio.getText()))){
                    c.insertarRango(Integer.parseInt(txtElemento.getText()));
                    txtResultado.setText(c.toStringRango());
                }else{
                JOptionPane.showMessageDialog(null, "El elemento que desea ingresar no forma parte del rango creado para el conjunto ");
                }
                
            }else{
                if(!RBDefinido.isSelected() && !RBRango.isSelected() && RBSinDef.isSelected()){
                    c.insertar2(Integer.parseInt(txtElemento.getText()));
                    txtResultado.setText(c.toString());
                }else{
                    JOptionPane.showMessageDialog(null, "Error de Insertar");
                }
            }
        }
        
    }//GEN-LAST:event_btnInsertarActionPerformed

